mapper.select:
select * from dish


        and name like concat('%',#{name},'%')


        and type = #{type}


        and type = #{status}


order by sort asc , create_time desc

mapper.update:
update category


        name = #{name},


        categoryId = #{categoryId},


        price = #{price},


        image = #{image},


        description = #{description}


        status = #{status}


        update_time = #{updateTime},


        update_user = #{updateUser}


where id = #{id}
